#include <iostream>
#include <monkey/monkey.h>

int main()
{
  monkey::Node root;
  root.insert("bibou", 3.8);
  root.insert("pouette", "A string");
  root.insert("The answer", 42);
  root.insert("The boubou", "cacahuette");
  std::cout << "Root type : " << root.type() << std::endl;
  std::cout << "Root size : " << root.size() << std::endl;

  const monkey::Node &bibou(root["bibou"]);
  std::cout << "Bibou type : " << bibou.type() << std::endl;

  for(auto iter = root.begin() ; iter != root.end() ; ++iter)
    std::cout << "-> '" << iter->first << "' : " << iter->second.as<std::string>() << std::endl;

  return 0;
}
